<template>
  <div>
    <h2>List of filtered fruits</h2>
      <div v-for="(fruit, index) in filteredFruits" :key="index">
        <span>{{ fruit.name }}</span>
      </div>
  </div>
</template>

<script>
export default {
    name: 'ComputedProperties',
    props: {},
    data() {
        return {
            fruits: [
                 { name: 'Apple', isFavorite : true, src: "data:image/jpeg;base64," },
                { name: 'Orange',  isFavorite : false, src: "data:image/jpeg;base64," },
                { name: 'Watermelon',  isFavorite : true, src: "https://assets.shop.loblaws.ca/products/20145379001/b2/en/front/20145379001_front_a06_@2.png"},
            ],
        } 
    },
    methods: {
    },
    computed : {
        filteredFruits() {
            return this.fruits.filter(fruit => fruit.isFavorite)
        }
    }
}
</script>

